					{
						Logging.WriteDiagnostic("Selecting 'Relic Weapon Atma Enhancement'.");
						SelectIconString.ClickLineContains("Relic Weapon Atma Enhancement");
					}
					else
					{
						Logging.WriteDiagnostic("Relic Weapon Atma Enhancement window failed to open.");
						ff14bot.TreeRoot.Stop("Window failed to open");
					}

					await Coroutine.Wait(10000, () => Talk.DialogOpen);
					await Buddy.Coroutines.Coroutine.Sleep(500);
